import { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  //fonksiyonelliğini genişlet ve app'i class component sayesinde
  constructor() {
    // p değişse buttona basılsa da bu constructor kurucu yöntem çalıştırsın Herşeyden önce constructor çalışır.
    super();
    this.state = {
      canavarlar: [],
    };
    console.log("1")
  }
  componentDidMount() { // başlangıç state değeri var sonra component did mount ile bağlandı
    console.log("3")
    fetch(`https://jsonplaceholder.typicode.com/users`)
      .then((res) => res.json()) //index.js react strict mode silinirse çift konsol ortadan kalkar
      .then((users) =>
        this.setState(
          () => {
            return { canavarlar: users };
          },
          () => {
            console.log(this.state);
          }
        )
      );
  }
  render() {
    return (
      <div className="App">
        <input 
        className="search-box" 
        type="search" 
        placeholder="search monsters"
        onChange={(e)=>{
        console.log(e.target.value)
        const aramaDizesi = e.target.value.toLocaleLowerCase()
        //[{name:"leanne"},{name:"Namık"}]
        const filtrelenmişCanavarlar = this.state.canavarlar.filter(
          (canavar)=>{
           return canavar.name.includes(aramaDizesi)
          }
        )
        this.setState(()=>{
          return {canavarlar:filtrelenmişCanavarlar}
        })
        }}/>
        {this.state.canavarlar.map((canavar) => {
          return (
            <div key={canavar.name}>
              <h1>{canavar.name}</h1>
            </div>
          );
        })}
      </div>
    );
  }
}

export default App;
